var Sequelize = require('sequelize');
var dotty = require('dotty');
var QueueModel = require('./SqlModel');

module.exports = SqlObj;

function SqlObj(config) {	
	this.sequelize = new Sequelize(config.name,config.username,config.password,config.options);	
}

SqlObj.prototype.model = function (modelName,attributes,options) {
	
	for ( var attributename in attributes) {

		switch (attributes[attributename].type) {
			case "String":
				attributes[attributename].type = Sequelize.STRING;
				break;
			case "Integer":
				attributes[attributename].type = Sequelize.INTEGER;
				break;
			default:
			}
		}	
	return new SqlModel(this.sequelize,modelName,attributes,options);
};


